namespace Zozothemes\ShopByBrand\Controller\Adminhtml\Items;

class Index extends \Zozothemes\ShopByBrand\Controller\Adminhtml\Items
{
    /**
     * Items list.
     *
     * @return \Magento\Backend\Model\View\Result\Page
     */
    public function execute()
    {
        /** @var \Magento\Backend\Model\View\Result\Page $resultPage */
        $resultPage = $this->resultPageFactory->create();
        $resultPage->setActiveMenu('zozothemes::base');
        $resultPage->getConfig()->getTitle()->prepend(__('Zozothemes ShopByBrand'));
        $resultPage->addBreadcrumb(__('Zozothemes'), __('Zozothemes'));
        $resultPage->addBreadcrumb(__('Items'), __('ShopByBrand'));
        return $resultPage;
    }
}
